  }

  return true;
}

function move(square, direction) {
  switch (direction) {
    case 'r':
      return [square[0] + 1, square[1]];

    case 'l':
      return [square[0] - 1, square[1]];

    case 'u':
      return [square[0], square[1] - 1];

